import React, { Component } from "react"
import styled from "styled-components"
import { Button } from "react-bootstrap"

const Text = styled.p`
    & > .metamask-title {
        font-size: 3vh;
    }

    & > .metamask-message {
        font-size: 1.5vh;
    }
`

const InstallWindow = styled.div`
    max-width: 50vw;
    max-height: 50vh;
    align-items: center;
    text-align: center;
    background-color: red;
    padding: 1vh 0.5vw 2vh 0.5vw;
`

const MetamaskImage = styled.img`
    max-width: 50vw;
    max-height: 50vh;
    height: auto;
    width: auto;
`

const BackButton = styled(Button)`
    color: palevioletred;
    font-size: 1em;
    margin: 1em;
    padding: 0.25em 1em;
    border: 2px solid palevioletred;
    border-radius: 3px;
`

class MetamaskComponent extends Component {

    onBackButtonPress = (event) => {
        event.preventDefault();
        console.log("siema");
    }

    render() {
        return (
            <InstallWindow>
                <div className="metamask-content">
                    <p className="image download-metamask">
                        <Text>
                            <p className="metamask-title">You have to install Metamask to use out token system!</p>
                            <p className="metamask-message">Click on the image below to install Metamask extension</p>
                        </Text>
                        <a href="https://metamask.io/" rel="noopener noreferrer" target="_blank">
                            <MetamaskImage src="https://metamask.io/img/metamask.png" alt="Metamask icon"/>
                        </a>
                    </p>
                    <BackButton onClick={this.onBackButtonPress}>Cześć!</BackButton>
                </div>
            </InstallWindow>
        )
    }
}

export default MetamaskComponent
