import React from 'react'
import styled from 'styled-components'


const MyMessage = styled.div`
        text-align: right;
        .date {
            font-size: 10px;
            color: #ecf0f1;
        }
        .content{
                font-size: 20px;
                padding: 6px;
        }
        .tag {
            display: inline;
            font-size: 12px;
            font-style: italic;
            color: gray;
        }
        display: inline;
        border-radius: 10px;
        padding: 4px;
        background-color: rgba(103, 84, 89, 1);
        float: right;
        margin: 3px;
        clear: both;
        max-width: 70%;
        min-width: 20%`;
const FriendMessage = styled.div`
        .date {
            font-size: 10px;
            color: #ecf0f1;
        }
        .content{
                font-size: 20px;
                padding: 6px;
        }
        .tag {
            display: inline;
            font-size: 12px;
            font-style: italic;
            color: gray;
        }
        display: inline;
        border-radius: 10px;
        padding: 4px;
        background-color: rgba(103, 84, 89, 0.7);
        float: left;
        margin: 3px;
        clear: both;
        max-width: 70%;
        min-width: 20%`;

function isImageMessage(message) {
    return /^("<input *type="image" *src=".*" *\/>"")$/.test(message);
}

function parseMessageContent(content) {
    if(isImageMessage(content)) {

        const imageLink = content.match(/src=".*"/g)
        // imageLink =

        return (
            <div className="content">{"Zdecydowanie obrazek"}</div>
        )
    } else {
        return (
            <div className="content">{content}</div>
        )
    }
}

function Message(props) {
    // getMessageContent = () => {
    //         return props.isActive
    //             ? <MyMessage
    //                 // onMouseOver={() => props.handleOver(props.id)}
    //             >
    //                     <div className={"date"}>{dateStr}</div>
    //                     <div className={"content"}>{props.model.content}</div>
    //                     {props.model.tags ? props.model.tags.map(tag => <div className={"tag"}>{`#${tag} `}</div>) : []}
    //             </MyMessage>
    //             : <FriendMessage
    //                 // onMouseOver={() => props.handleOver(props.id)}
    //             >
    //                     <div className={"date"}>{dateStr}</div>
    //                     <div className={"content"}>{props.model.content}</div>
    //                     {props.model.tags ? props.model.tags.map(tag => <div className={"tag"}>{`#${tag}`}</div>) : []}
    //             </FriendMessage>
    // };

    const dt = new Date(props.model.date);
    const dateStr = `${dt.getDay()}.${dt.getMonth()}.${dt.getFullYear()} ${dt.getHours()}:${dt.getMinutes()}`;

    return props.isActive
        ? <MyMessage
            // onMouseOver={() => props.handleOver(props.id)}
        >
            {/*<div className={"date"}>{dateStr}</div>*/}
            {parseMessageContent(props.model.content)}
            {props.model.tags ? props.model.tags.map(tag => <div className={"tag"}>{`#${tag} `}</div>) : []}
        </MyMessage>
        : <FriendMessage
            // onMouseOver={() => props.handleOver(props.id)}
        >
            {/*<div className={"date"}>{dateStr}</div>*/}
            {parseMessageContent(props.model.content)}
            {props.model.tags ? props.model.tags.map(tag => <div style={{color: '#e5cc92'}}
                                                                 className={"tag"}>{`#${tag}`}</div>) : []}
        </FriendMessage>
}

export default Message
