import React, { Component } from "react"
import styled from "styled-components"

const Text = styled.p`
    font-size: 50px;
`

const InstallWindow = styled.div`
    max-width: 50vw;
    max-height: 50vh;
    align-items: center;
    text-align: center;
    background-color: red;
`

class MetamaskComponent extends Component {
    render() {
        return (
            <InstallWindow>
                <div className="model-content">
                    <p className="image download-metamask">
                        <Text>
                            <h1>You have to install Metamask to use out token system!</h1>
                            <h2>Click on the image below to install Metamask extension</h2>
                        </Text>
                        <Text></Text>
                        <a href="https://metamask.io/" rel="noopener noreferrer" target="_blank">
                            <img src="https://metamask.io/img/metamask.png" alt="Metamask icon"/>
                        </a>
                    </p>
                </div>
            </InstallWindow>
        )
    }
}

export default MetamaskComponent
